import React, { useState, useRef, useEffect } from "react";

const CameraComponent = ({ onCapture }) => {
  const [stream, setStream] = useState(null);
  const [photoData, setPhotoData] = useState(null);
  const [permissionStatus, setPermissionStatus] = useState("prompt"); // 'prompt', 'granted', 'denied'
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // Sprawdź status uprawnień przy ładowaniu komponentu
  useEffect(() => {
    checkCameraPermission();
  }, []);

  // Funkcja sprawdzająca uprawnienia do aparatu
  const checkCameraPermission = async () => {
    try {
      // W nowszych przeglądarkach możemy sprawdzić status uprawnień
      if (navigator.permissions && navigator.permissions.query) {
        const result = await navigator.permissions.query({ name: "camera" });
        setPermissionStatus(result.state);

        // Nasłuchuj zmian statusu uprawnień
        result.addEventListener("change", () => {
          setPermissionStatus(result.state);
        });
      }
    } catch (err) {
      console.error("Błąd sprawdzania uprawnień:", err);
    }
  };

  // Funkcja do uruchamiania kamery
  const startCamera = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: "environment" },
        audio: false,
      });

      setStream(mediaStream);
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }

      // Jeśli udało się uzyskać dostęp, oznacza to, że uprawnienia zostały przyznane
      setPermissionStatus("granted");
    } catch (err) {
      console.error("Błąd dostępu do kamery:", err);
      setPermissionStatus("denied");
    }
  };

  // Zatrzymanie kamery przy odmontowaniu komponentu
  useEffect(() => {
    return () => {
      if (stream) {
        stream.getTracks().forEach((track) => track.stop());
      }
    };
  }, [stream]);

  // Funkcja do robienia zdjęcia
  const capturePhoto = () => {
    if (!videoRef.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    const context = canvas.getContext("2d");
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    // Dodaj znacznik czasu do zdjęcia
    context.font = "14px Arial";
    context.fillStyle = "white";
    context.strokeStyle = "black";
    context.lineWidth = 0.5;

    const timestamp = new Date().toLocaleString();
    context.fillText(`Data: ${timestamp}`, 10, 20);
    context.strokeText(`Data: ${timestamp}`, 10, 20);

    const data = canvas.toDataURL("image/jpeg");
    setPhotoData(data);

    // Zatrzymaj kamerę po zrobieniu zdjęcia
    if (stream) {
      stream.getTracks().forEach((track) => track.stop());
      setStream(null);
    }

    if (onCapture) {
      onCapture(data);
    }
  };

  // Funkcja do resetowania zdjęcia
  const resetPhoto = () => {
    setPhotoData(null);
    if (stream) {
      stream.getTracks().forEach((track) => track.stop());
      setStream(null);
    }
    startCamera(); // Uruchom kamerę ponownie
  };

  // Renderowanie w zależności od statusu uprawnień
  if (permissionStatus === "denied") {
    return (
      <div className="camera-component">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          <p className="font-bold">Brak dostępu do aparatu</p>
          <p>
            Aby korzystać z tej funkcji, musisz zezwolić na dostęp do aparatu.
          </p>
          <p className="mt-2">
            Jak to zrobić:
            <ul className="list-disc pl-5 mt-1">
              <li>Kliknij ikonę kłódki/ustawień obok adresu URL</li>
              <li>Znajdź ustawienia aparatu</li>
              <li>Zmień ustawienie na "Zezwól"</li>
              <li>Odśwież stronę</li>
            </ul>
          </p>
          <button
            onClick={startCamera}
            className="mt-4 bg-primary text-white px-4 py-2 rounded-lg hover:bg-opacity-90 transition"
          >
            Spróbuj ponownie
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="camera-component">
      {!photoData && permissionStatus !== "granted" && (
        <div className="camera-permission">
          <p className="mb-4">
            Aby zrobić zdjęcie, aplikacja potrzebuje dostępu do aparatu.
          </p>
          <button
            onClick={startCamera}
            className="bg-primary text-white px-4 py-2 rounded-lg hover:bg-opacity-90 transition"
          >
            Zezwól na dostęp do aparatu
          </button>
        </div>
      )}

      {!photoData && permissionStatus === "granted" && !stream && (
        <div className="camera-start">
          <button
            onClick={startCamera}
            className="bg-primary text-white px-4 py-2 rounded-lg hover:bg-opacity-90 transition"
          >
            Uruchom aparat
          </button>
        </div>
      )}

      {!photoData && stream && (
        <div className="camera-view">
          <video
            ref={videoRef}
            autoPlay
            playsInline
            className="w-full rounded-lg"
          />
          <button
            onClick={capturePhoto}
            className="mt-4 bg-primary text-white px-4 py-2 rounded-lg hover:bg-opacity-90 transition"
          >
            Zrób zdjęcie
          </button>
        </div>
      )}

      {photoData && (
        <div className="photo-preview">
          <img
            src={photoData}
            alt="Zrobione zdjęcie"
            className="w-full rounded-lg"
          />
          <div className="flex space-x-4 mt-4">
            <button
              onClick={resetPhoto}
              className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-opacity-90 transition"
            >
              Zrób ponownie
            </button>
          </div>
        </div>
      )}

      <canvas ref={canvasRef} style={{ display: "none" }} />
    </div>
  );
};

export default CameraComponent;
