import React, { useState, useRef, useEffect } from 'react';

const CameraComponent = ({ onCapture, onCancel }) => {
  const videoRef = useRef(null);
  const [stream, setStream] = useState(null);
  const [hasPermission, setHasPermission] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    // Funkcja do inicjalizacji kamery
    const initCamera = async () => {
      try {
        const constraints = {
          video: {
            facingMode: 'environment', // Używaj tylnej kamery
            width: { ideal: 1280 },
            height: { ideal: 720 }
          }
        };

        const mediaStream = await navigator.mediaDevices.getUserMedia(constraints);
        setStream(mediaStream);
        setHasPermission(true);

        if (videoRef.current) {
          videoRef.current.srcObject = mediaStream;
        }
      } catch (err) {
        console.error('Błąd dostępu do kamery:', err);
        setHasPermission(false);
        setErrorMessage(`Nie można uzyskać dostępu do kamery: ${err.message}`);
      }
    };

    initCamera();

    // Czyszczenie przy odmontowaniu komponentu
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  const takePhoto = () => {
    if (!videoRef.current) return;

    const video = videoRef.current;
    const canvas = document.createElement('canvas');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    const ctx = canvas.getContext('2d');
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

    // Konwertuj obraz do formatu base64
    const imageDataUrl = canvas.toDataURL('image/jpeg');

    // Zatrzymaj strumień kamery
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
    }

    // Przekaż zdjęcie do komponentu nadrzędnego
    onCapture(imageDataUrl);
  };

  if (hasPermission === null) {
    return <div className="p-4 text-center">Oczekiwanie na dostęp do kamery...</div>;
  }

  if (hasPermission === false) {
    return (
      <div className="p-4 text-center">
        <p className="text-red-500 mb-4">{errorMessage}</p>
        <p>Aby korzystać z tej funkcji, musisz zezwolić na dostęp do kamery.</p>
        <button 
          className="mt-4 bg-primary text-white px-4 py-2 rounded"
          onClick={onCancel}
        >
          Powrót
        </button>
      </div>
    );
  }

  return (
    <div className="camera-container flex flex-col items-center">
      <video 
        ref={videoRef} 
        autoPlay 
        playsInline 
        className="w-full max-w-md rounded-lg shadow-lg"
      />

      <div className="camera-controls mt-4 flex justify-center space-x-4">
        <button 
          onClick={takePhoto}
          className="bg-primary text-white px-6 py-3 rounded-full shadow-lg"
        >
          Zrób zdjęcie
        </button>
        <button 
          onClick={onCancel}
          className="bg-gray-500 text-white px-6 py-3 rounded-full shadow-lg"
        >
          Anuluj
        </button>
      </div>
    </div>
  );
};

export default CameraComponent;
