import React, { useState, useRef, useEffect } from "react";

const CameraComponent = ({ onCapture }) => {
  const [stream, setStream] = useState(null);
  const [photoData, setPhotoData] = useState(null);
  const [showCamera, setShowCamera] = useState(false);
  const [error, setError] = useState("");
  const [location, setLocation] = useState(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // Pobierz lokalizację przy ładowaniu komponentu
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
        },
        (err) => {
          console.error("Błąd pobierania lokalizacji:", err);
          setError(
            "Nie udało się pobrać lokalizacji. Upewnij się, że usługi lokalizacji są włączone."
          );
        }
      );
    }
  }, []);

  // Funkcja do uruchamiania kamery
  const startCamera = async () => {
    setError("");
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: "environment" },
        audio: false,
      });
      setStream(mediaStream);
      if (videoRef.current) {
        videoRef.current.srcObject = mediaStream;
      }
      setShowCamera(true);
    } catch (err) {
      console.error("Błąd dostępu do kamery:", err);
      setError(
        "Nie udało się uzyskać dostępu do kamery. Upewnij się, że przeglądarka ma uprawnienia do aparatu."
      );
    }
  };

  // Zatrzymanie kamery przy odmontowaniu komponentu
  useEffect(() => {
    return () => {
      if (stream) {
        stream.getTracks().forEach((track) => track.stop());
      }
    };
  }, [stream]);

  // Funkcja do robienia zdjęcia
  const capturePhoto = () => {
    if (!videoRef.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    const context = canvas.getContext("2d");
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    // Dodaj znacznik czasu i lokalizację do zdjęcia
    context.font = "14px Arial";
    context.fillStyle = "white";
    context.strokeStyle = "black";
    context.lineWidth = 0.5;

    const timestamp = new Date().toLocaleString();
    context.fillText(`Data: ${timestamp}`, 10, 20);
    context.strokeText(`Data: ${timestamp}`, 10, 20);

    if (location) {
      const locationText = `Lokalizacja: ${location.latitude.toFixed(
        6
      )}, ${location.longitude.toFixed(6)}`;
      context.fillText(locationText, 10, 40);
      context.strokeText(locationText, 10, 40);
    }

    const data = canvas.toDataURL("image/jpeg");
    setPhotoData(data);

    // Zatrzymaj kamerę po zrobieniu zdjęcia
    if (stream) {
      stream.getTracks().forEach((track) => track.stop());
      setStream(null);
    }

    setShowCamera(false);

    if (onCapture) {
      onCapture(data, {
        timestamp: new Date().toISOString(),
        location: location,
      });
    }
  };

  // Funkcja do resetowania zdjęcia
  const resetPhoto = () => {
    setPhotoData(null);
    setShowCamera(false);
    if (stream) {
      stream.getTracks().forEach((track) => track.stop());
      setStream(null);
    }
  };

  return (
    <div className="camera-component">
      {error && (
        <div className="error-message bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
          <p className="mt-2">
            Wskazówki:
            <ul className="list-disc pl-5">
              <li>Upewnij się, że przeglądarka ma uprawnienia do aparatu</li>
              <li>
                Spróbuj zainstalować aplikację jako PWA (dodaj do ekranu
                głównego)
              </li>
              <li>Użyj przeglądarki Chrome lub Safari</li>
            </ul>
          </p>
        </div>
      )}

      {!photoData && !showCamera && (
        <div className="camera-options flex flex-col space-y-4">
          <button
            onClick={startCamera}
            className="bg-primary text-white px-4 py-2 rounded-lg hover:bg-opacity-90 transition"
          >
            Zrób zdjęcie aparatem
          </button>

          <p className="text-sm text-gray-600 mt-2">
            Zdjęcie musi być zrobione na bieżąco, aby zapewnić uczciwą
            weryfikację. Zostanie oznaczone datą, godziną i lokalizacją.
          </p>
        </div>
      )}

      {showCamera && (
        <div className="camera-view">
          <video
            ref={videoRef}
            autoPlay
            playsInline
            className="w-full rounded-lg"
          />
          <button
            onClick={capturePhoto}
            className="mt-4 bg-primary text-white px-4 py-2 rounded-lg hover:bg-opacity-90 transition"
          >
            Zrób zdjęcie
          </button>
        </div>
      )}

      {photoData && (
        <div className="photo-preview">
          <img
            src={photoData}
            alt="Zrobione zdjęcie"
            className="w-full rounded-lg"
          />
          <div className="flex space-x-4 mt-4">
            <button
              onClick={resetPhoto}
              className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-opacity-90 transition"
            >
              Zrób ponownie
            </button>
          </div>
        </div>
      )}

      <canvas ref={canvasRef} style={{ display: "none" }} />
    </div>
  );
};

export default CameraComponent;
